        public static string GetIssueNew(int lType)
        {
            string sql = "select top(1)Issue from LotteryRecord where lType = " + lType + " order by Issue desc";
            long nextIssue = long.Parse(SqlHelper.ExecuteScalar(sql).ToString()) + 1;

            string startTime = "";
            string endTime = "";
            string firstIssue = "001";
            DateTime d = DateTime.Now;

            string date = d.ToString("yyyy-MM-dd");




            if (lType == 21)
            {
                startTime = " 8:30:0";
                endTime = " 22:10:0";
            }
            //else if (lType == 25)
            //{
            //    startTime = " 10:20:0";
            //    endTime = " 2:0:0";
            //}
            else if (lType == 61 || lType == 25)
            {
                startTime = " 0:1:0";
            }


            DateTime d1 = DateTime.Parse(date + startTime);
            DateTime d2 = DateTime.Parse(date + endTime);


            if (lType == 21)
            {
                if (d < d1)
                {
                    return d.ToString("MMdd") + firstIssue;
                }
                else if (d > d2)
                {
                    return d.AddDays(1).ToString("MMdd") + firstIssue;
                }

            }
            //else if (lType == 25)
            //{

            //    if (d > d2 && d < d1)
            //    {
            //        return d.ToString("MMdd") + firstIssue;
            //    }

            //}
            else if (lType == 61 || lType == 25)
            {
                if (d < d1)
                {
                    return d.ToString("MMdd") + "0001";
                }
            }




            return nextIssue.ToString().Substring(4);


        }


