            }

            if (showSecond < 10)
            {
                showSecondFormat = "0" + showSecond;
            }
            else
            {
                showSecondFormat = showSecond + "";
            }

            return showHourFormat + "&" + showMinuteFormat + "&" + showSecondFormat;
        }


