        public static string AddSecond(string result, int sec)
        {
            //特殊情况
            if (result == "已封盘") return "00&00&" + sec;

            string[] arr = result.Split('&');

            int minute = int.Parse(arr[1]);
            int second = int.Parse(arr[2]);



            second += sec;
            if (second >= 60)
            {
                minute += second / 60;
                second = second % 60;
            }

            string showMin = "";

            if (minute >= 10)
            {
                showMin = minute.ToString();
            }
            else
            {
                showMin = "0" + minute;
            }

            string showSec = "";

            if (second >= 10)
