        //必须是数字
        public static bool JudgeShuZi(string str)
        {
            //Regex reg1 = new Regex(@"[A-Za-z]+[0-9]+");

            Regex reg1 = new Regex(@"[0-9]{11}");
            return reg1.IsMatch(str);
