        //判断必须同时包含数字和字母
        public static bool JudgeTongShiBaoHanShuziAndZiMu(string str)
        {
            //Regex reg1 = new Regex(@"[A-Za-z]+[0-9]+");

            Regex reg1 = new Regex(@"^(([A-Za-z]+[0-9]+)|([0-9]+[A-Za-z]+))[A-Za-z0-9]*$");

            return reg1.IsMatch(str);
        }


        //判断是不是中文
        public static bool JudgeIsZhongWen(string content)
        {
            string regexstr = @"[\u4e00-\u9fa5]";

            string pattern = @"^\d*$";

            bool flag = true;

            foreach (char c in content)
            {
                if (c != content[content.Length - 1])
                {
                    if (!Regex.IsMatch(c.ToString(), regexstr))
                    {
                        flag = false;
                        break;
                    }
                }
                else
                {
                    if (!Regex.IsMatch(c.ToString(), regexstr) && !Regex.IsMatch(c.ToString(), pattern))
                    {
                        flag = false;
                        break;
                    }
                }

            }

            return flag;
        }


