        public static bool JudgeBetCorrect(int lType, string betInfo)
        {
            string[] arr = betInfo.Split('|');
            string betNum = arr[2];
            string[] betArr = betNum.Split(',');

            //特殊情况
            if (betNum.StartsWith("+") || betNum.StartsWith("-"))
            {
                return false;
            }

            if (lType == 3 || lType == 4)
            {
                #region 六合彩

                #region 特码 正码 正特

                if (betInfo.Contains("特码") || betInfo.Contains("正A") || betInfo.Contains("正B") ||
                    betInfo.Contains("正1特") || betInfo.Contains("正2特") || betInfo.Contains("正3特") ||
                    betInfo.Contains("正4特") || betInfo.Contains("正5特") || betInfo.Contains("正6特"))
                {
                    arr = betInfo.Split('$'); //多注

                    foreach (string s in arr)
                    {
                        string[] arr2 = s.Split('|');
                        betNum = arr2[2];

                        if (IsNumberic(betNum)) //如果是数字
                        {
                            betArr = betNum.Split(',');

