                {
                    result = JieCheng(count) / (JieCheng(4) * JieCheng(count - 4));
                }
                else if (playName == "五不中")
                {
                    result = JieCheng(count) / (JieCheng(5) * JieCheng(count - 5));
                }
                else if (playName == "六不中")
                {
                    result = JieCheng(count) / (JieCheng(6) * JieCheng(count - 6));
                }
                else if (playName == "七不中")
